$.validator.setDefaults({
	highlight : function(e) {
		$(e).closest(".form-group").removeClass("has-success").addClass(
				"has-error")
	},
	success : function(e) {
		e.closest(".form-group").removeClass("has-error").addClass(
				"has-success")
	},
	errorElement : "span",
	errorPlacement : function(e, r) {
		e.appendTo(r.is(":radio") || r.is(":checkbox") ? r.parent().parent()
				.parent() : r.parent())
	},
	errorClass : "help-block m-b-none",
	validClass : "help-block m-b-none"
});

$().ready(function() {
	var e = "<i class='fa fa-times-circle'></i> ";
	$("#adduntisForm").validate({

		rules : {
			cityId : {
				required : true,
				min : 0
			},
			untisName : "required",
			coefficient : {
				required : true,
				range : [ 0, 100 ],
			},
			address : "required",
			admin : "required",
			tel : {
				required : true,
				digits : true,
				minlength : 3
			}
		},
		messages : {
			cityId : {
				min : "请选择城市"
			},
			coefficient : "请输入0-100之间的数",
			tel : "请输入正确的电话号码"

		}
	}

	);

	$("#addProductForm").validate({

		rules : {

			deviceId : {
				required : true,
				digits : true,
				minlength : 4,
				maxlength : 4
			},
			productName : "required",
			price : {
				required : true,
				number : true,
				min : 0
			},
			img : {
				required : true,
				url : true
			}
		},
		messages : {
			deviceId : "4位数字组成",
			price : "请输入合法金额",
			img : "请输入合法的url地址"

		}
	}

	);

	$("#adduserForm").validate({

		rules : {

			users : {
				required : true,
				minlength : 3	
			},
			password :{
				required : true,
				rangelength:[6,16]			
			},
		password1:{
			required : true,
			equalTo:"#password",
		},
			price : {
				required : true,
				number : true,
				min : 0
			},
			email : {
				required : true,
				email:true
			},
			tel : {
				required : true,
				digits : true,
				minlength : 3
			},
		 
		},
		messages : {
			users : "不低于3位",
			password : "密码长度6-16位",
			password1 : "两次输入密码不一致",
			email:"请输入合法的邮箱",
			tel : "请输入正确的电话号码"
			

		}
	}

	);

});
